#!/bin/bash
# configure.sh
#
# This script can be used to set up your Kubernetes configuration
# in the Linux tools container.
#

mkdir ~/.kube
cat > ~/.kube/config <<EOF
# <some config here>
apiVersion: v1
clusters:
- cluster:
    certificate-authority-data: 
    server: https://192.168.1.48:6443
  name: kubernetes
contexts:
- context:
    cluster: kubernetes
    user: kubernetes-admin
  name: kubernetes-admin@kubernetes
current-context: kubernetes-admin@kubernetes
kind: Config
preferences: {}
users:
- name: kubernetes-admin
  user:
    client-certificate-data: 
    client-key-data: 
EOF
